    nav {
      margin: 50px;
      display: flex;
      justify-content: space-between;
      align-items: center;
  
      .nav__links {
        width: 30%;
        display: flex;
        justify-content: space-between;
        align-items: center;
  
        
      }
  
      
    }
  
    .mainContent {
      .introSection {
        height: 40vh;
        flex-wrap: wrap;
        justify-content: center;
        align-items: flex-start;
  
        //Limits width so each element can flex wrap correctly
        .introSection__title {
          width: 45%;
        }
  
        .introSection__image {
          width: 45%;
          //display flex to make image visible - was none
          display: flex;
        }
      }
  
      .aboutMeSection {
        background-color: $aboutme-color;
        height: 50vh;
        flex-wrap: wrap;
        margin: 0;
        //Similar logic as previous in changing the width so it fills the new page size.
        .aboutMeSection__aboutMeText {
          width: 50%;
        }
  
        .aboutMeSection__title--desktop {
          display: flex;
          margin-bottom: 2%;
          margin-top: 5%;
        }
  
        .aboutMeSection__image--desktop {
          display: flex;
          width: 40vw;
          max-width: 800px;
        }
  
        .aboutMeSection__image--mobile {
          display: none;
        }
  
        .aboutMeSection__title--mobile {
          display: none;
        }
      }
  
      .skillsSection {
        width: 100%;
  
        .skillsSection__icons {
          display: flex;
          flex-direction: row;
          flex-wrap: wrap;
          justify-content: center;
          align-self: center;
          width: 90%;
          max-width: 90%;
  
          img {
            height: 5%;
            width: 5%;
            margin: 3%;
          }
        }
      }
  
      .projectsSection {
        width: 80vw;
  
        //Switched to display grid, instead of being display: flex; so it fits the page better.
        .projectsSection__contentContainer {
          display: grid;
          grid-template-columns: 1fr 1fr 1fr;
          grid-template-rows: 1fr 1fr;
          grid-gap: 2%;
  
          //Adds these projects into view as their previous display was none.
          #project5,
          #project6 {
            display: block;
          }
  
          button {
            align-items: left;
          }
        }
      }
    }
  
    footer {
      margin-top: 0%;
      display: flex;
      flex-direction: row-reverse;
    }
  }

